class ToggleLike{constructor(t){this.toggler=t,this.toggleLike()}toggleLike(){$(this.toggler).click((function(t){t.preventDefault();$.ajax({type:"POST",url:$(this).attr("href")}).done((function(t){let e=t.data.post.likes.length;1==t.data.deleted?$("#put-like-"+t.data.post._id).html('<i class="far fa-thumbs-up"></i> Like'):$("#put-like-"+t.data.post._id).html('<i class="fas fa-thumbs-up"></i> Like'),e>0?$("#likes-count-"+t.data.post._id).html('<img src="/images/like-4113758d1a.png" \n                            data-likes="<%= post.likes.length %>" id="count" class="rounded-circle" width="30" > '+e):$("#likes-count-"+t.data.post._id).html("")})).fail((function(t){console.log("error in completing the request")}))}))}}